sys_script_include.script:
var RITMVariablesUtil = Class.create();
RITMVariablesUtil.prototype = {
    initialize: function() {},

    getVariables: function(ritmSysID, includeEmpty, replaceReferences) {

        includeEmpty = includeEmpty ? includeEmpty : false;
        replaceReferences = replaceReferences ? replaceReferences : false;

        var variables = [];

        var scReqItemRec = new GlideRecord('sc_req_item');
        if (scReqItemRec.get(ritmSysID)) {

            // Regular RITM variables
            var varElements = scReqItemRec.variables.getElements();
            for (var i = 0; i < varElements.length; i++) {
                var question = varElements[i].getQuestion();
                var qValue = question.getValue();
                var qDisplayValue = question.getDisplayValue();
                var qType = question.getType();

                // Check for qDisplayValue too because for unchecked checkboxes getValue() returns null
                if (includeEmpty || qValue || qDisplayValue) {
                    var qOut = {};
                    qOut.name = question.getName();
                    qOut.description = question.getLabel();

                    if (replaceReferences && qType == 8) {
                        // type = 8 - reference
                        qOut.value = qDisplayValue;
                    } else {
                        qOut.value = qValue;
                        if (qValue != qDisplayValue) {
                            qOut.displayValue = qDisplayValue;
                        }
                    }

                    variables.push(qOut);
                }
            }

            // Multi Row RITM variables
            var vars = scReqItemRec.variables.getElements(true);

            for (var i = 0; i < vars.length; i++) {
                var mrvs = vars[i];
                if (mrvs.isMultiRow()) {
                    var mrvsArray = JSON.parse(mrvs);
                    var mOut = {};
                    mOut.name = mrvs.getLabel();
                    mOut.value = mrvsArray;
                    variables.push(mOut);
                }
            }
        }

        return variables;
    },
    getTags: function(ritmSysID) {
        var scReqItemRec = new GlideRecord('sc_req_item');
        if (scReqItemRec.get(ritmSysID)) {

            var tag_array = [{
                    "name": "Build Date",
                    "value": ""
                },
                {
                    "name": "Component",
                    "value": ""
                },
                {
                    "name": "Kyn_mcms_managed",
                    "value": scReqItemRec.variable_pool.managed.toString()
                },
                {
                    "name": "Environment",
                    "value": scReqItemRec.variable_pool.environment.toString()
                },
                {
                    "name": "Patching Group",
                    "value": scReqItemRec.variable_pool.patching_group.toString()
                },
                {
                    "name": "OS",
                    "value": scReqItemRec.variable_pool.os.toString()
                }
            ];

            var tags = {
                "tags": tag_array
            }

        }
        return tag_array;

    },
    getOSdetails: function(img) {

        try {
            var str = img.toString();           
            var osdetails = '';
            if ((str.includes("windows") || str.includes("Windows ") || str.includes("Win")) && (str.includes("2012"))) {
                osdetails = 'Windows';

            } else if ((str.includes("windows") || str.includes("Windows ") || str.includes("Win")) && (str.includes("2016"))) {
                osdetails = 'Windows';


            } else if ((str.includes("windows") || str.includes("Windows ") || str.includes("Win")) && (str.includes("2019"))) {
                osdetails = 'Windows';

            } else if ((str.includes("Red Hat Enterprise Linux 6") || str.includes("RHEL 6") || str.includes("Linux 6") || str.includes("RHEL6") || str.includes("RHEL") || str.includes("Red Hat Enterprise Linux")) && (str.includes("6"))) {
                osdetails = 'Linux';

            } else if ((str.includes("Red Hat Enterprise Linux 7") || str.includes("Linux 7") || str.includes("RHEL7") || str.includes("RHEL-7") || str.includes("RHEL") || str.includes("Red Hat Enterprise Linux")) && (str.includes("7"))) {
                osdetails = 'Linux';


            } else if ((str.includes("Red Hat Enterprise Linux 8") || str.includes("Linux 8") || str.includes("RHEL8") || str.includes("RHEL 8") || str.includes("RHEL") || str.includes("Red Hat Enterprise Linux")) && (str.includes("8"))) {
                osdetails = 'Linux';


            }
            return osdetails;
        } catch (e) {
            return e;
        }
    },

    type: 'RITMVariablesUtil'
};
